class SimpleForm_Validators_Integer extends SimpleForm_Core_Validator{public function Validate($submitValue,$fieldName,SimpleForm_Core_Field&$field){$submitValue=trim($submitValue);$safeValue=preg_replace("#[^0-9]#",'',$submitValue);if(mb_strlen($safeValue)!==mb_strlen($submitValue)){$this->addError($field,SimpleForm::$DefaultMessages[SimpleForm::INTEGER],function($msg,$args){return SimpleForm_Core_View::Format($msg,$args);});}return$safeValue;}}
class SimpleForm_Validators_Url extends SimpleForm_Core_Validator{public function Validate($submitValue,$fieldName,SimpleForm_Core_Field&$field){$submitValue=trim($submitValue);$safeValue=filter_var($submitValue,FILTER_VALIDATE_URL);$safeValue=$safeValue===FALSE?'':$safeValue;if(mb_strlen($safeValue)!==mb_strlen($submitValue)){$this->addError($field,SimpleForm::$DefaultMessages[SimpleForm::URL],function($msg,$args){return SimpleForm_Core_View::Format($msg,$args);});}return$safeValue;}}
